mapper.update:
UPDATE board
SET title = #{title}
        , content = #{content}
        , writer = #{writer}
        , updatedate = CURRENT_TIMESTAMP
WHERE board_no = #{boardNo}

mapper.insert:
INSERT INTO board (
    title
    , content
    , writer
    , createdate
    , updatedate
) VALUES (
    #{title}
    , #{content}
    , #{writer}
    , CURRENT_TIMESTAMP
    , CURRENT_TIMESTAMP
)
