mapper.delete:
UPDATE member
SET flag = 'Y'
WHERE id = #{id};

mapper.update:
UPDATE member
SET password = #{password}
WHERE id = #{id};

UPDATE member
SET id = #{id}
    , name = #{name}
    , birth = #{birth}
    , gender = #{gender}
    , phone = #{phone}
    , email = #{email}
WHERE id = #{oldId};

mapper.insert:
INSERT INTO member (
    id
    , password
    , name
    , birth
    , gender
    , phone
    , email
    , flag
) VALUES (
    #{id}
    , #{password}
    , #{name}
    , #{birth}
    , #{gender}
    , #{phone}
    , #{email}
    , 'N'
);
