mapper.select:
SELECT COUNT(*)
FROM comment
WHERE board_no = #{boardNo} AND c_depth = 0 AND remv_flag = 'N';

mapper.update:
UPDATE comment
SET remv_flag = 'Y'
WHERE comment_no = #{commentNo};

UPDATE comment
SET comment = #{comment}
WHERE comment_no = #{commentNo};

mapper.insert:
INSERT INTO comment (
      board_no
    , writer
    , comment
    , c_depth
    , c_group
    , createdate
    , remv_flag
) VALUES (
     #{boardNo}
    , #{writer}
    , #{comment}
    , 1
    , #{cgroup}
    , CURRENT_TIMESTAMP
    , 'N'
);

INSERT INTO comment (
    board_no
    , writer
    , comment
    , c_depth
    , c_group
    , createdate
    , remv_flag
) VALUES (
    #{boardNo}
    , #{writer}
    , #{comment}
    , 0
    , 0
    , CURRENT_TIMESTAMP
    , 'N'
);
